import React from 'react';

const QuestionBase = ({data: {question,correct_answer,incorrect_answers,category,difficulty}}) => (
    <div>
        <h1 className="text-5xl font-bold">{category}</h1>
        <p className="mt-10 text-2xl">Level: {difficulty}</p>
        <div className="p-8">
            <h2 className="text-2xl" dangerouslySetInnerHTML={{__html: question}}/>
        </div>
        <div className="flex flex-col items-center mt-4 space-y-4 font-semibold ">
            <button className="p-4 rounded-lg w-48 border-2 border-blue-800 uppercase">{correct_answer}</button>
            <button className="p-4 rounded-lg w-48 border-2 border-blue-800 uppercase">{incorrect_answers}</button>
        </div>
    </div>
);

export default QuestionBase;
