import { createSlice } from '@reduxjs/toolkit'

export const questionsSlice = createSlice({
  name: 'questions',
  initialState: {
    questions: [],
  },
  reducers: {
    setQuestions: (state, action) => {
      state.questions = action.payload
    },
    setQuestionAnswerStatus: (state, action) => {
      const question = state.questions.find((question) => question.id === action.payload.id);
      question.isCorrect = action.payload.answer === question.correct_answer;
    }
  }
})

// Action creators are generated for each case reducer function
export const { setQuestionAnswerStatus, setQuestions } = questionsSlice.actions

export default questionsSlice.reducer
