import React,{ useState, useEffect } from 'react';

const [questions, setQuestions] = useState([]);

  useEffect(async ()=>{
    fetch(API_URL)
      .then((res)=> res.json())
      .then((data) => {
        setQuestions(data.results);
      });
  },[]);
const Question = () => (
    <div>
        <h1 className="text-5xl font-bold">{questions[0].category}</h1>
        <p className="mt-10 text-2xl">Level: {questions[0].difficulty}</p>
        <div className="p-8">
            <h2 className="text-2xl" dangerouslySetInnerHTML={{__html: questions[0].question}}/>
        </div>
        <div className="flex flex-col items-center mt-4 space-y-4 font-semibold ">
            <button className="p-4 rounded-lg w-48 border-2 border-blue-800 uppercase">{questions[0].correct_answer}</button>
            <button className="p-4 rounded-lg w-48 border-2 border-blue-800 uppercase">{questions[0].incorrect_answers}</button>
        </div>
    </div>
)

export default Question;
