import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

export const pokemonApi = createApi({
  baseQuery: fetchBaseQuery({ baseUrl: 'https://opentdb.com/api.php' }),
  endpoints: (builder) => ({
    getPokemonByName: builder.query({
      query: (amountValue, difficultyValue) => { console.log('amountValue', amountValue); console.log('difficultyValue', difficultyValue); return `?amount=10&difficulty=hard&type=boolean` },
    }),
  }),
})

// Export hooks for usage in functional components
export const { useGetPokemonByNameQuery } = pokemonApi
