import React from 'react';

const Button = ({answer}) => (
    <button className="p-4 rounded-lg w-48 border-2 border-blue-800 uppercase hover:bg-blue-800 hover:text-white">{answer}</button>
);

const QuestionBase = ({ data: {category,difficulty,correct_answer,incorrect_answers,question}}) => {(
    <div>
        <h1 className="text-5xl font-bold">{category}</h1>
        <p className="mt-10 text-2xl">Level: {difficulty}</p>
        <div className="p-8">
            <h2 className="text-2xl" dangerouslySetInnerHTML={{ __html: question}}/>
        </div>
        <div className="flex flex-col items-center mt-4 space-y-4 font-semibold ">
            <Button onClick={() => handleAnwer(true)} answer={correct_answer}/>
            <Button onClick={() => handleAnwer(false)} answer={incorrect_answers}/>
        </div>
    </div>
)};

export default QuestionBase;
