import React, { useState, useEffect } from 'react';

const API_URL = 'https://opentdb.com/api.php?amount=10&difficulty=easy&type=boolean';

function App() {
  const [questions, setQuestions] = useState([]);

  useEffect(async ()=>{
    fetch(API_URL)
      .then((res)=> res.json())
      .then((data) => {
        setQuestions(data.results);
      });
  },[]);
  return <div className="container">
    <div className="bg-white text-blue-800 p-8">
      <h2 className="text-3xl">{questions[0].question}</h2>
    </div>
    <div className="flex flex-col items-center mt-4 space-y-4">
      <button className="bg-white p-4 text-blue-800 font-semibold rounded-lg w-48 border-2 border-blue-800 uppercase">{questions[0].correct_answer}</button>
      <button className="bg-white p-4 text-blue-800 font-semibold rounded-lg w-48 border-2 border-blue-800 uppercase">{questions[0].incorrect_answers}</button>
    </div>
  </div>;
}

export default App;
