import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";

export const questionsApi = createApi({
  baseQuery: fetchBaseQuery({ baseUrl: "https://opentdb.com/api.php" }),
  endpoints: (builder) => ({
    getQuestionsByParams: builder.query({
      query: ({ difficultyValue, amountValue }) => {
        console.log("getQuestionsByParams amountValue", amountValue);
        console.log("getQuestionsByParams difficultyValue", difficultyValue);
        return `?amount=10&difficulty=hard&type=boolean`;
      },
    }),
  }),
});

export const { useGetQuestionsByParamsQuery } = questionsApi;
